mapper.select:
select  

from demo_function where 1=1 


    and func_no  LIKE #{pattern_funcNo}



    and func_name  LIKE #{pattern_funcName}



    and func_type = #{funcType}



    and type = #{type}



    and state = #{state}



    and version = #{version}



    and created_time >= #{startDate}



    and created_time <= #{endDate}

order by id desc

select 

from demo_function
where func_no = #{funcNo} and version = #{version}
